mapper.update:
update
    an_uac_department_role
set
    department_id=#{departmentId}
where
    role_id=#{roleId}

mapper.select:
SELECT

FROM an_uac_department_role dr
WHERE dr.role_id = #{roleId, jdbcType=BIGINT}

SELECT

FROM an_uac_department_role dr
WHERE dr.department_id = #{departmentId, jdbcType=BIGINT}
