mapper.update:
update
    an_uac_department_user
set
    department_id=#{departmentId}
where
    user_id=#{userId}

mapper.select:
SELECT

FROM an_uac_department_user du
WHERE du.user_id = #{userId, jdbcType=BIGINT}

SELECT

FROM an_uac_department_user du
WHERE du.department_id = #{departmentId, jdbcType=BIGINT}
